package simplilearn;
/*13.	Write a program to calculate the sum of first 10 natural number.  */
public class A2Q13 {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int sum=0;
      for (int i= 0; i <10;i++)
    	  
      {
    	sum = sum  +i;
    	System.out.println(i);
    	
    	
      }
      System.out.println("Sum is : "+sum);
	}

}
